import React, { useContext } from 'react'

import GlobalContext from '../Context/GlobalContext'

const  BalanceSection = () => {

  const {transactions,saveTransaction} = useContext(GlobalContext)
    const balance = transactions.reduce((p,c) =>{
      return p + c.amount
    },0)


    const handleSubmit = (e) =>{
        e.preventDefault()
        saveTransaction()
    }
  return (
 <div className='container'>
     <div className='box-one'>
          <p>CURRENT BALANCE</p>
          <h2>â‚¹{balance}</h2>
      </div>
      <form onSubmit={handleSubmit} >
    <span> <label htmlFor = "input">Enter Transaction</label>
     <input type="text"  id='input'/></span>
   <span>  <label htmlFor = "input"> Enter Amount</label>
     <input type="number"  id='input'/>
     <button className='save-btn'>SAVE TRANSACTIONS <i className="bi bi-save-fill"></i></button>
   </span>
   </form>
 </div>
  )
}

export default BalanceSection
