import React, { useContext, useState } from 'react'

import GlobalContext from '../Context/GlobalContext'
import { saveTransaction } from '../Context/GlobalAction'
import { toast } from "react-toastify";


const  BalanceSection = () => {

  const {transactions,dispatch} = useContext(GlobalContext)
    const balance = transactions.reduce((p,c) =>{
      return p + c.amount
    },0)

     const [text , setText] = useState("")
     const [amount , setAmount] = useState("")
    const handleSubmit = (e) =>{
        e.preventDefault()

        const newTransaction = {
          id : crypto.randomUUID(),
          text,
          amount : parseInt(amount) 
        }
         const data = saveTransaction(newTransaction)
         dispatch({
          type : "SAVE",
          payload : data
       })
      
       toast.success("Transaction Saved",('ðŸ¦„ Wow so easy!', {
        position: "top-center",
        autoClose: 500,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
        }))

        setText("")
        setAmount("")
    }
  return (
 <div className='container'>
    
      <form onSubmit={handleSubmit} >
      <svg   xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320"><path fill="#0099ff" fill-opacity="1" d="M0,288L48,272C96,256,192,224,288,197.3C384,171,480,149,576,165.3C672,181,768,235,864,250.7C960,267,1056,245,1152,250.7C1248,256,1344,288,1392,304L1440,320L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path></svg>

     <label id='label' htmlFor = "input">Enter Transaction</label>
     <input required value={text} type="text"  id='input'onChange={(e)=>setText(e.target.value)}/>
    <label id='label' htmlFor = "input"> Enter Amount</label>
     <input required value={amount} type="number"   id='input'onChange={(e)=>setAmount(e.target.value)}/>
     <button className='save-btn'>SAVE TRANSACTIONS <i className="bi bi-save-fill"></i></button>
   
   </form>
   <div className='box-one'>
          <p>CURRENT BALANCE</p>
          <h2>â‚¹{balance}</h2>
      </div>
 </div>
  )
}

export default BalanceSection
